contracts/DiscountV1.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC4626.sol";
import "./curve/ICurvePool.sol";
import "./spectra/Commands.sol";
import "./spectra/Constants.sol";
import "./spectra/IPrincipalToken.sol";
import "./spectra/IRegistry.sol";
import "./spectra/IRouter.sol";

contract DiscountV1 is Initializable, PausableUpgradeable, OwnableUpgradeable {
    IRegistry public registry;
    mapping(address => uint256) private buyFee; //1000 = 10%
    mapping(address => uint256) private sellEarlyFee;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address _initialOwner,
        address _registry
    ) public initializer {
        __Pausable_init();
        __Ownable_init(_initialOwner);
        registry = IRegistry(_registry);
    }

    function calcAmountAfterFee(
        uint256 _amount,
        uint256 _feePercentage
    ) private pure returns (uint256) {
        return (_amount * (10000 - _feePercentage) * 10 ** 14) / 10 ** 18;
    }

    function previewBuyDiscountedAsset(
        uint256 _amount,
        address _curvePoolAddress,
        uint256 _IBTindexInCurve,
        uint256 _PTindexInCurve
    ) external view returns (uint256) {
        (
            bytes memory commands,
            bytes[] memory inputs,
            uint256 amountAfterFee,

        ) = _prepareBuyDiscountedAsset(
                _amount,
                _curvePoolAddress,
                _IBTindexInCurve,
                _PTindexInCurve,
                0
            );

        IRouter router = IRouter(registry.getRouter());
        return
            (router.previewRate(commands, inputs) * amountAfterFee) / 10 ** 27;
    }

    function buyDiscountedAsset(
        uint256 _amount,
        address _curvePoolAddress,
        uint256 _IBTindexInCurve,
        uint256 _PTindexInCurve,
        uint256 _minAmountOut
    ) external whenNotPaused {
        (
            bytes memory commands,
            bytes[] memory inputs,
            uint256 amountAfterFee,
            address underlyingToken
        ) = _prepareBuyDiscountedAsset(
                _amount,
                _curvePoolAddress,
                _IBTindexInCurve,
                _PTindexInCurve,
                _minAmountOut
            );

        IRouter router = IRouter(registry.getRouter());
        IERC20(underlyingToken).transferFrom(
            address(msg.sender),
            address(this),
            _amount
        );
        IERC20(underlyingToken).approve(address(router), amountAfterFee);
        router.execute(commands, inputs);
    }

    function _prepareBuyDiscountedAsset(
        uint256 _amount,
        address _curvePoolAddress,
        uint256 _IBTindexInCurve,
        uint256 _PTindexInCurve,
        uint256 _minAmountOut
    )
        private
        view
        returns (
            bytes memory commands,
            bytes[] memory inputs,
            uint256 amountAfterFee,
            address underlyingToken
        )
    {
        address IBT = ICurvePool(_curvePoolAddress).coins(_IBTindexInCurve);
        underlyingToken = IERC4626(IBT).asset();

        commands = abi.encodePacked(
            bytes1(uint8(Commands.TRANSFER_FROM)),
            bytes1(uint8(Commands.DEPOSIT_ASSET_IN_IBT)),
            bytes1(uint8(Commands.CURVE_SWAP))
        );

        inputs = new bytes[](3);

        amountAfterFee = calcAmountAfterFee(_amount, buyFee[IBT]);

        // Input for TRANSFER_FROM
        inputs[0] = abi.encode(underlyingToken, amountAfterFee);
        // Input for DEPOSIT_ASSET_IN_IBT
        inputs[1] = abi.encode(
            IBT,
            Constants.CONTRACT_BALANCE,
            Constants.ADDRESS_THIS // recipient
        );
        // Input for CURVE_SWAP
        inputs[2] = abi.encode(
            _curvePoolAddress,
            _IBTindexInCurve, //inputToken index
            _PTindexInCurve, //outputToken index
            Constants.CONTRACT_BALANCE, // amountIn
            _minAmountOut, // minAmountOut
            address(msg.sender) // recipient
        );
    }

    function redeemDiscountedAsset(
        address _ptToken,
        uint256 _amount
    ) external whenNotPaused {
        IPrincipalToken(_ptToken).redeem(_amount, msg.sender, msg.sender);
    }

    function previewClaimPTEarly(
        address _curvePool,
        uint256 _PTindexInCurve,
        uint256 _IBTindexInCurve,
        uint256 _amount
    ) external view returns (uint256) {
        (bytes memory commands, bytes[] memory inputs, address ptAddress) = _prepareClaimPTEarly(
            _curvePool,
            _PTindexInCurve,
            _IBTindexInCurve,
            _amount,
            0 // _minAmountOut is 0 since it's a preview
        );

        IRouter router = IRouter(registry.getRouter());
        uint256 userClaim = (router.previewRate(commands, inputs) * _amount) /
            10 ** 27;

        return calcAmountAfterFee(userClaim, sellEarlyFee[ptAddress]);
    }

    function claimPTEarly(
        address _curvePool,
        uint256 _PTindexInCurve,
        uint256 _IBTindexInCurve,
        uint256 _amount,
        uint256 _minAmountOut
    ) external whenNotPaused {
        (
            bytes memory commands,
            bytes[] memory inputs,
            address ptAddress
        ) = _prepareClaimPTEarly(
                _curvePool,
                _PTindexInCurve,
                _IBTindexInCurve,
                _amount,
                _minAmountOut
            );

        address underlyingToken = IPrincipalToken(ptAddress).underlying();
        uint256 feeBalanceBeforeClaim = IERC20(underlyingToken).balanceOf(
            address(this)
        );

        IRouter router = IRouter(registry.getRouter());
        IERC20(ptAddress).transferFrom(
            address(msg.sender),
            address(this),
            _amount
        );
        IERC20(ptAddress).approve(address(router), _amount);
        router.execute(commands, inputs);
        uint256 userClaim = IERC20(underlyingToken).balanceOf(address(this)) -
            feeBalanceBeforeClaim;
        IERC20(underlyingToken).transfer(
            address(msg.sender),
            calcAmountAfterFee(userClaim, sellEarlyFee[ptAddress])
        );
    }

    function _prepareClaimPTEarly(
        address _curvePool,
        uint256 _PTindexInCurve,
        uint256 _IBTindexInCurve,
        uint256 _amount,
        uint256 _minAmountOut
    )
        private
        view
        returns (bytes memory commands, bytes[] memory inputs, address PT)
    {
        PT = ICurvePool(_curvePool).coins(_PTindexInCurve);
        address underlyingToken = IPrincipalToken(PT).underlying();
        address IBT = ICurvePool(_curvePool).coins(_IBTindexInCurve);

        commands = abi.encodePacked(
            bytes1(uint8(Commands.TRANSFER_FROM)),
            bytes1(uint8(Commands.CURVE_SWAP)),
            bytes1(uint8(Commands.REDEEM_IBT_FOR_ASSET)),
            bytes1(uint8(Commands.ASSERT_MIN_BALANCE)),
            bytes1(uint8(Commands.TRANSFER))
        );

        inputs = new bytes[](5);

        // Input for TRANSFER_FROM
        inputs[0] = abi.encode(PT, _amount);
        // Input for CURVE_SWAP
        inputs[1] = abi.encode(
            _curvePool,
            _PTindexInCurve,
            _IBTindexInCurve,
            _amount,
            _minAmountOut,
            Constants.ADDRESS_THIS // recipient
        );
        // Input for REDEEM_IBT_FOR_ASSET
        inputs[2] = abi.encode(
            IBT,
            Constants.CONTRACT_BALANCE, //shares
            Constants.ADDRESS_THIS // recipient
        );
        // Input for ASSERT_MIN_BALANCE
        inputs[3] = abi.encode(
            underlyingToken,
            Constants.ADDRESS_THIS,
            _minAmountOut
        );
        // Input for TRANSFER
        inputs[4] = abi.encode(
            underlyingToken,
            address(this), // recipient
            Constants.CONTRACT_BALANCE // amount
        );
    }

    function getBuyFee(address _IBT) external view returns (uint256) {
        return buyFee[_IBT];
    }

    function getSellEarlyFee(address _PT) external view returns (uint256) {
        return sellEarlyFee[_PT];
    }

    // OWNER ONLY FUNCTIONS
    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function setBuyFee(address _IBT, uint256 _newFee) external onlyOwner {
        buyFee[_IBT] = _newFee; //1000 = 10% fee
    }

    function setSellEarlyFee(address _PT, uint256 _newFee) external onlyOwner {
        sellEarlyFee[_PT] = _newFee; //1000 = 10% fee
    }

    function updateRegistry(address _newRegistry) external onlyOwner {
        registry = IRegistry(_newRegistry);
    }

    function collectFee(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(owner(), _amount);
    }
}

